hod_2="off" gutter_width="1.3"][et_pb_column type="1_2"][et_pb_slider admin_label="Slider" show_arrows="on" show_pagination="on" auto="off" auto_ignore_hover="off" parallax="on" parallax_method="on" remove_inner_shadow="on" background_position="default" background_size="default" hide_content_on_mobile="off" hide_cta_on_mobile="off" show_image_video_mobile="off" custom_button="off" button_letter_spacing="0" button_use_icon="default" button_icon_placement="right" button_on_hover="on" button_letter_spacing_hover="0"] [et_pb_slide button_text="Track Your Government!" button_link="http://electionpromisestracker.in/governments" background_position="default" background_size="default" background_color="#ffffff" image="http://www.electionpromisestracker.in/wp-content/uploads/2017/05/fact-check.jpg" use_bg_overlay="on" use_text_overlay="off" alignment="center" background_layout="light" allow_player_pause="off" text_border_radius="3" header_font_select="default" header_font="||||" body_font_select="default" body_font="||||" custom_button="off" button_font_select="default" button_font="||||" button_use_icon="default" button_icon_placement="right" button_on_hover="on"] [/et_pb_slide] [/et_pb_slider][/et_pb_column][et_pb_column type="1_2"][et_pb_post_slider admin_label="Post Slider" posts_number="5" include_categories="1" orderby="date_desc" show_arrows="on" show_pagination="on" show_more_button="on" content_source="off" use_manual_excerpt="on" show_meta="on" background_layout="light" show_image="on" image_placement="background" parallax="off" parallax_method="off" use_bg_overlay="on" use_text_overlay="off" remove_inner_shadow="off" background_position="default" background_size="default" auto="off" auto_ignore_hover="off" hide_content_on_mobile="off" hide_cta_on_mobile="off" show_image_video_mobile="off" meta_letter_spacing="0" custom_button="off" button_letter_spacing="0" button_use_icon="default" button_icon_placement="right" button_on_hover="on" button_letter_spacing_hover="0" background_color="#ffffff" body_line_height="2.4em"] [/et_pb_post_slider][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section bb_built="1" admin_label="section" inner_shadow="off" parallax="off"][et_pb_row admin_label="row"][et_pb_column type="4_4"][et_pb_divider admin_label="Divider" color="#27323a" show_divider="on" height="5" divider_style="solid" divider_position="top" hide_on_mobile="on"] [/et_pb_divider][/et_pb_column][/et_pb_row][et_pb_row admin_label="row" make_fullwidth="off" use_custom_width="off" width_unit="on" use_custom_gutter="off" allow_player_pause="off" parallax="off" parallax_method="off" make_equal="off" parallax_1="off" parallax_method_1="off" parallax_2="off" parallax_method_2="off"][et_pb_column type="1_2"][et_pb_image admin_label="Image" src="http://www.electionpromisestracker.in/wp-content/uploads/2017/03/Promises-1.png" show_in_lightbox="off" url_new_window="off" animation="left" use_overlay="off" sticky="off" align="left" force_fullwidth="off" always_center_on_mobile="on" use_border_color="off" border_color="#ffffff" border_style="solid"] [/et_pb_image][/et_pb_column][et_pb_column type="1_2"][et_pb_text admin_label="Text" background_layout="light" text_orientation="left" use_border_color="off" border_color="#ffffff" border_style="solid"]\r\n<h1>What we do!</h1>\r\n[/et_pb_text][et_pb_blurb admin_label="Blurb" title="Monitor Government Progress" url_new_window="off" use_icon="on" font_icon="%%160%%" icon_color="#ffffff" use_circle="on" use_circle_border="off" circle_border_color="#2ea3f2" icon_placement="left" animation="top" background_layout="light" text_orientation="center" circle_color="#2ea3f2" use_icon_font_size="off" use_border_color="off" border_color="#ffffff" border_style="solid"]\r\n\r\nOur effort is to enable voters to monitor the performance of their governments. We will also assess if governments adhere to all of their promises in regard to education, health, economy,agriculture, transparency, law and order, minority rights, culture, and many more.\r\n\r\n[/et_pb_blurb][et_pb_blurb admin_label="Blurb" title="Fact-Check Claims" url_new_window="off" use_icon="on" font_icon="%%49%%" icon_color="#ffffff" use_circle="on" use_circle_border="off" circle_border_color="#2ea3f2" icon_placement="left" animation="top" background_layout="light" text_orientation="center" url="Fact checking any statement or claim issued by our elected representatives. No opinion, Only 100% verified Facts" circle_color="#2ea3f2" use_icon_font_size="off" use_border_color="off" border_color="#ffffff" border_style="solid"]\r\n\r\nFact checking any statement or claim issued by our elected representatives. No opinion, Only 100% verified Facts\r\n\r\n[/et_pb_blurb][et_pb_blurb admin_label="Blurb" title="Discuss &amp; Spread" url_new_window="off" use_icon="on" font_icon="%%222%%" icon_color="#ffffff" use_circle="on" use_circle_border="off" circle_border_color="#2ea3f2" icon_placement="left" animation="top" background_layout="light" text_orientation="center" circle_color="#2ea3f2" use_icon_font_size="off" use_border_color="off" border_color="#ffffff" border_style="solid"]\r\n\r\nEnable community dialogue, and help spread right information to the world.\r\n\r\n[/et_pb_blurb][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section bb_built="1" admin_label="section" fullwidth="off" specialty="off" inner_shadow="off" parallax="off"][et_pb_row admin_label="row"][et_pb_column type="4_4"][et_pb_divider admin_label="Divider" color="#27323a" show_divider="on" height="30" divider_style="solid" divider_position="top" hide_on_mobile="on"] [/et_pb_divider][/et_pb_column][/et_pb_row][et_pb_row admin_label="Row"][et_pb_column type="4_4"][et_pb_text admin_label="Text" background_layout="light" text_orientation="center" use_border_color="off" border_color="#ffffff" border_style="solid"]\r\n<h1>Our Vision!</h1>\r\nTo track every government on every sector by 2020. This is a work in progress and we require your help in tracking the perform