(1194, 7, '2017-06-02 14:25:43', '2017-06-02 08:55:43', '<p>[et_pb_section bb_built="1" admin_label="Section" fullwidth="on" specialty="off"][et_pb_fullwidth_header admin_label="Fullwidth Header" title="Articles" background_layout="light" text_orientation="center" header_fullscreen="off" header_scroll_down="off" parallax="off" parallax_method="off" content_orientation="center" image_orientation="center" custom_button_one="off" button_one_letter_spacing="0" button_one_use_icon="default" button_one_icon_placement="right" button_one_on_hover="on" button_one_letter_spacing_hover="0" custom_button_two="off" button_two_letter_spacing="0" button_two_use_icon="default" button_two_icon_placement="right" button_two_on_hover="on" button_two_letter_spacing_hover="0" background_color="#f7f7f7"] [/et_pb_fullwidth_header][/et_pb_section][et_pb_section bb_built="1" admin_label="section"][et_pb_row admin_label="row"][et_pb_column type="1_2"][et_pb_blurb admin_label="Blurb" title="NewsLaundary Collaborates with EPT" url="http://bit.ly/2rkDDdM" url_new_window="on" use_icon="on" font_icon="%%71%%" icon_color="#2ea3f2" use_circle="off" circle_color="#2ea3f2" use_circle_border="off" circle_border_color="#2ea3f2" icon_placement="left" animation="top" background_layout="light" text_orientation="justified" use_icon_font_size="off" use_border_color="off" border_color="#ffffff" border_style="solid"]</p><p><span style="font-weight: 400;"><img class="size-medium aligncenter" src="https://www.newslaundry.com/uploads/2017/05/Modi-illustration-article-image.jpg" width="1200" height="520" />Newslaundry collaborates with Election Promises Tracker to publish a report card of Narendra Modi''s 3 year performance. All the information is sources from Election Promises Tracker''s website. Read on to get a glimpse of Modi''s campaign promises...</span></p><p>[/et_pb_blurb][/et_pb_column][et_pb_column type="1_2"][et_pb_blurb admin_label="Blurb" title="Helter Skelter" url="https://newsd.in/health-promises-he